mapper.select:
select 

from dic
where type = #{type,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}

mapper.delete:
delete from dic
where type = #{type,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}

mapper.update:
update dic


    name = #{name,jdbcType=VARCHAR},


    weight = #{weight,jdbcType=INTEGER},


where type = #{type,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}

update dic
set name = #{name,jdbcType=VARCHAR},
  weight = #{weight,jdbcType=INTEGER}
where type = #{type,jdbcType=VARCHAR}
  and code = #{code,jdbcType=VARCHAR}
